import React, { useState } from 'react';
import {
  Fab,
  TextField,
  TextareaAutosize,
  Typography,
  Grid,
} from '@material-ui/core';
import { ArrowBack, GetApp } from '@material-ui/icons';
import { Link } from 'react-router-dom';
import QRcode from 'qrcode.react';

import './home.css';

function QRgenerator() {
  const [qr, setQr] = useState('Enter Equipment Name Here');
  const handleChange = (event) => {
    setQr(event.target.value);
  };
  const downloadQR = () => {
    const canvas = document.getElementById('myqr');
    const pngUrl = canvas
      .toDataURL('image/png')
      .replace('image/png', 'image/octet-stream');
    let downloadLink = document.createElement('a');
    downloadLink.href = pngUrl;
    downloadLink.download = 'myqr.png';
    document.body.appendChild(downloadLink);
    downloadLink.click();
    document.body.removeChild(downloadLink);
  };

  return (
    <div className='center'>
      <Grid container spacing={2}>
        <Grid item xs>
          <Link to='/'>
            <Fab className='bt' style={{ marginRight: 10 }} color='primary'>
              <ArrowBack />
            </Fab>
          </Link>
          <Typography className='heading' variant='h3' m={20}>
            QR GENERATOR
          </Typography>
        </Grid>

        <Grid item xs>
          <div style={{ marginTop: 30 }}>
            <TextField
              onChange={handleChange}
              style={{ width: 320 }}
              value={qr}
              label='QR content'
              size='large'
              variant='outlined'
              color='primary'
            />
          </div>

          <div>
            {qr ? (
              <QRcode id='myqr' value={qr} size={320} includeMargin={true} />
            ) : (
              <p>No QR code preview</p>
            )}
          </div>
          <div>
            {qr ? (
              <Grid container>
                <Grid item xs={10}>
                  {/* <TextareaAutosize
                  style={{ fontSize: 18, width: 250, height: 100 }}
                  rowsMax={4}
                  defaultValue={qr}
                  value={qr}
                /> */}
                </Grid>
                <Grid item xs={2}>
                  <Fab
                    onClick={downloadQR}
                    style={{ marginLeft: 10 }}
                    color='primary'
                    className='bt'
                  >
                    <GetApp />
                  </Fab>
                </Grid>
              </Grid>
            ) : (
              ''
            )}
          </div>
        </Grid>
      </Grid>
    </div>
  );
}

export default QRgenerator;
